{% block stylesheets %}
     <link href=\"/bundles/easyadmin/app.css\" rel=\"stylesheet\" />
{% endblock %}

{% block title %}DocThor - Zaloguj się!{% endblock %}

{% block body %}
<form method=\"post\">

    <center>
    <h1><img src=\"thor.png\">DocThor</h1><br><br>
    <h3 class=\"h3 mb-3 font-weight-normal\">Zaloguj się do DocThor</h3>
    <table><tr><td>
    <input type=\"text\" placeholder=\"Użytkownik\" value=\"{{ last_username }}\" name=\"username\" id=\"inputUsername\" class=\"form-control\" autocomplete=\"username\" required autofocus></td></tr><tr>
<td>
    <input type=\"password\" placeholder=\"Hasło\" name=\"password\" id=\"inputPassword\" class=\"form-control\" autocomplete=\"current-password\" required></td></tr></table>

    {% if error %}
        <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
    {% endif %}

    {% if app.user %}
        <div class=\"mb-3\">
            Jesteś zalogowany jako {{ app.user.username }}, <a href=\"{{ path('app_logout') }}\">Wyloguj</a>
        </div>
    {% endif %}

    <input type=\"hidden\" name=\"_csrf_token\"
           value=\"{{ csrf_token('authenticate') }}\">
    <a href=\"{{ path('app_forgot_password_request') }}\" class=\"-- link-color\">Zapomniałem hasła</a><br>

    {#
        Uncomment this section and add a remember_me option below your firewall to activate remember me functionality.
        See https://symfony.com/doc/current/security/remember_me.html

        <div class=\"checkbox mb-3\">
            <label>
                <input type=\"checkbox\" name=\"_remember_me\"> Remember me
            </label>
        </div>
    #}
<br>
    <button class=\"btn btn-primary\" type=\"submit\">
        Zaloguj
    </button>
</form>
</center>
{% endblock %}
